import {FiMenu} from "react-icons/fi";
import {FaPlay, FaGithub, FaLinkedin} from "react-icons/fa";
import {ImYoutube2} from "react-icons/im";
import {TiArrowSortedDown} from "react-icons/ti";
import goblin from './img/goblin_.png';
import {useEffect} from "react";
import ScrollReveal from "scrollreveal";
import Header from "./Components/Header";

function App() {
  // const handleClick = () =>{
  //     console.log('isClicked!')
  // }
  //
  // const menuToggle = () =>{
  //     let menu = document.querySelector('#menu-icon')
  //     let nav = document.querySelector('.navlist')
  //     menu.classList.toggle('bx-x')
  //     nav.classList.toggle('open')
  // }

  useEffect( () => {
      const sr = ScrollReveal({
          distance: '65px',
          duration: 2600,
          delay: 450,
          reset: true
      })

      sr.reveal('.hero-text', {delay:200, origin:'top'})
      sr.reveal('.hero-img', {delay:450, origin:'top'})
      sr.reveal('.icons', {delay:500, origin:'left'})
      sr.reveal('.scroll-down', {delay:500, origin:'right'})

    }, [])



  return (
    <div className="App">
      <Header/>

      <section className={'hero'}>
          <div className={'hero-text'}>
              <h5>#2 Trending</h5>
              <h4>Lead Supercell</h4>
              <h1>GOBLINS</h1>
              <p>Join millions of players worldwide as you build your village, <br/>
                  raise a clan, and compete in epic Clan Wars!</p>
              <a href={'/'} onClick={handleClick}>Compare</a>
              <a href={'/'} onClick={handleClick} className={'ctaa'}><FaPlay id={'faPlay'}/> Watch Gameplay</a>
          </div>

          <div className={'hero-img'}>
              <img src={goblin} alt={'GoblinImg'}/>
          </div>
      </section>

      <div className={'icons'}>
          <a href={'/'}><FaGithub /></a>
          <a href={'/'}><FaLinkedin /></a>
          <a href={'/'}><ImYoutube2 /></a>
      </div>

      <div className={'scroll-down'}>
          <a href={'/'} id={'i'}><TiArrowSortedDown/></a>

      </div>

    </div>
  );
}

export default App;
